// App.tsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddSlotForm from './components/AddSlotForm';
import SlotCalendar from './components/SlotCalendar';
import StudentView from './components/StudentView';
import RecordFeedback from './components/RecordFeedback';
import './App.css';

interface Slot {
  id: number;
  startTime: string;
  endTime: string;
  bookedBy?: string;
  studentPhone?: string;
  coachPhone?: string;
  satisfaction?: number;
  notes?: string;
  feedbackSubmitted?: boolean;
}

interface Student {
  id: number;
  name: string;
  phone: string;
}

function App() {
  const [slots, setSlots] = useState<Slot[]>([]);
  const [students, setStudents] = useState<Student[]>([]);
  const [currentStudent, setCurrentStudent] = useState<Student | null>(null);
  const [isCoachView, setIsCoachView] = useState(true);
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [viewAvailableSlots, setViewAvailableSlots] = useState(true);

  useEffect(() => {
    const fetchSlots = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/slots');
        setSlots(response.data);
      } catch (error) {
        console.error('Error fetching slots:', error);
      }
    };
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/students');
        setStudents(response.data);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };
    fetchSlots();
    fetchStudents();
  }, []);

  const handleAddSlot = (newSlot: Slot) => {
    setSlots([...slots, newSlot]);
  };

  const handleBookSlot = (updatedSlot: Slot) => {
    setSlots(slots.map(slot => slot.id === updatedSlot.id ? updatedSlot : slot));
  };

  const handleFeedbackSubmit = (updatedSlot: Slot) => {
    const updatedSlots = slots.map(slot => slot.id === updatedSlot.id ? { ...updatedSlot, feedbackSubmitted: true } : slot);
    setSlots(updatedSlots);
    setSelectedSlot(null);
  };

  const handleEditSlot = (slot: Slot) => {
    setSelectedSlot(slot);
  };

  const handleClearSlots = async () => {
    try {
      await axios.post('http://localhost:5001/api/slots/clear');
      setSlots([]);
      setSelectedSlot(null); // Reset right column
    } catch (error) {
      console.error('Error clearing slots:', error);
    }
  };

  const handleCreateStudent = async () => {
    const name = prompt("Enter the student's name:");
    const phone = prompt("Enter the student's phone number (XXX-XXX-XXXX):");
    if (name && phone) {
      try {
        const response = await axios.post('http://localhost:5001/api/students', { name, phone });
        setStudents([...students, response.data]);
        setCurrentStudent(response.data); // Set the new student as the current student
      } catch (error) {
        console.error('Error creating student:', error);
      }
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Stepful Scheduling</h1>
        <button className="header-button" onClick={() => setIsCoachView(!isCoachView)}>
          Switch to {isCoachView ? 'Student View' : 'Coach View'}
        </button>
        <button className="header-button" onClick={handleClearSlots}>Clear All Slots</button>
      </header>
      <div className="content">
        {isCoachView ? (
          <>
            <div className="column">
              <AddSlotForm onSubmit={handleAddSlot} existingSlots={slots} />
              <SlotCalendar slots={slots} onEdit={handleEditSlot} />
            </div>
            <div className="column">
              {selectedSlot ? (
                <RecordFeedback key={selectedSlot.id} slot={selectedSlot} onSubmit={handleFeedbackSubmit} />
              ) : (
                <p>Select a slot to view details and add or edit feedback.</p>
              )}
            </div>
          </>
        ) : (
          <>
            <div className="column">
              <h2>Student View</h2>
              <div>
                <label>
                  Select Student:
                  <select onChange={(e) => setCurrentStudent(students.find(s => s.id === Number(e.target.value)) || null)}>
                    {students.map(student => (
                      <option key={student.id} value={student.id}>{student.name}</option>
                    ))}
                  </select>
                </label>
                <button onClick={handleCreateStudent}>Add Student</button>
                <button onClick={() => setViewAvailableSlots(!viewAvailableSlots)}>
                  {viewAvailableSlots ? 'View My Slots' : 'View Available Slots'}
                </button>
              </div>
              {currentStudent && (
                <StudentView
                  slots={viewAvailableSlots ? slots : slots.filter(slot => slot.bookedBy === currentStudent.name)}
                  onBookSlot={handleBookSlot}
                  student={currentStudent}
                />
              )}
            </div>
            <div className="column">
              {selectedSlot ? (
                <RecordFeedback key={selectedSlot.id} slot={selectedSlot} onSubmit={handleFeedbackSubmit} />
              ) : (
                <p>Select a slot to view details or book.</p>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
