// StudentView.tsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Slot {
  id: number;
  startTime: string;
  endTime: string;
  bookedBy?: string;
  studentPhone?: string;
  coachPhone?: string;
  satisfaction?: number;
  notes?: string;
  feedbackSubmitted?: boolean;
}

interface Student {
  id: number;
  name: string;
  phone: string;
}

interface StudentViewProps {
  slots: Slot[];
  onBookSlot: (updatedSlot: Slot) => void;
  students: Student[];
  currentStudent: Student | null;
  setCurrentStudent: (student: Student | null) => void;
  handleCreateStudent: (name: string, phone: string) => void;
}

const StudentView: React.FC<StudentViewProps> = ({
  slots,
  onBookSlot,
  students,
  currentStudent,
  setCurrentStudent,
  handleCreateStudent,
}) => {
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [studentName, setStudentName] = useState('');
  const [studentPhone, setStudentPhone] = useState('');
  const [viewMySlots, setViewMySlots] = useState(false);

  const handleBook = async (slot: Slot) => {
    if (!currentStudent) {
      alert('Please select a student.');
      return;
    }
    try {
      const response = await axios.post(`http://localhost:5001/api/slots/${slot.id}/book`, {
        bookedBy: currentStudent.name,
        studentPhone: currentStudent.phone,
        studentId: currentStudent.id,
      });
      onBookSlot(response.data);
      setSelectedSlot(null); // Clear the selected slot
    } catch (error) {
      console.error('Error booking slot:', error);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric'
    });
  };

  const handleAddStudent = async () => {
    try {
      handleCreateStudent(studentName, studentPhone);
      setStudentName('');
      setStudentPhone('');
    } catch (error) {
      console.error('Error adding student:', error);
    }
  };

  return (
    <div className="content">
      <div className="column">
        <h2>Students</h2>
        <select onChange={(e) => setCurrentStudent(students.find(student => student.id === parseInt(e.target.value)) || null)}>
          <option value="">Select a student</option>
          {students.map(student => (
            <option key={student.id} value={student.id}>
              {student.name}
            </option>
          ))}
        </select>
        <h2>Add Student</h2>
        <input
          type="text"
          value={studentName}
          onChange={(e) => setStudentName(e.target.value)}
          placeholder="Student Name"
        />
        <input
          type="tel"
          value={studentPhone}
          onChange={(e) => setStudentPhone(e.target.value)}
          placeholder="Student Phone"
        />
        <button onClick={handleAddStudent}>Add Student</button>
        <button onClick={() => setViewMySlots(!viewMySlots)}>
          {viewMySlots ? 'View Available Slots' : 'View My Slots'}
        </button>
        <h2>{viewMySlots ? 'My Slots' : 'Available Slots'}</h2>
        <ul>
          {(viewMySlots ? slots.filter(slot => slot.bookedBy === currentStudent?.name) : slots.filter(slot => !slot.bookedBy)).map(slot => (
            <li key={slot.id} className={`${slot.bookedBy ? 'booked' : ''} ${slot.feedbackSubmitted ? 'feedback-submitted' : ''}`}>
              {formatDate(slot.startTime)} - {formatDate(slot.endTime)}
              {!slot.bookedBy && <button onClick={() => setSelectedSlot(slot)}>Book</button>}
              {slot.bookedBy && <button onClick={() => setSelectedSlot(slot)}>View Details</button>}
            </li>
          ))}
        </ul>
      </div>
      <div className="column">
        <h2>My Slots</h2>
        <ul>
          {slots.filter(slot => slot.bookedBy === currentStudent?.name).map(slot => (
            <li key={slot.id} className={`${slot.bookedBy ? 'booked' : ''} ${slot.feedbackSubmitted ? 'feedback-submitted' : ''}`}>
              {formatDate(slot.startTime)} - {formatDate(slot.endTime)}
              <button onClick={() => setSelectedSlot(slot)}>View Details</button>
            </li>
          ))}
        </ul>
        {selectedSlot && (
          <div>
            <h2>Slot Details</h2>
            {selectedSlot.bookedBy ? (
              <>
                <p><strong>Booked by:</strong> {selectedSlot.bookedBy}</p>
                <p><strong>Student's phone:</strong> {selectedSlot.studentPhone}</p>
                <p><strong>Coach's phone:</strong> {selectedSlot.coachPhone}</p>
              </>
            ) : (
              <form onSubmit={(e) => {
                e.preventDefault();
                handleBook(selectedSlot);
              }}>
                <label>
                  Your Name:
                  <input
                    type="text"
                    value={currentStudent?.name || ''}
                    readOnly
                  />
                </label>
                <label>
                  Your Phone:
                  <input
                    type="tel"
                    value={currentStudent?.phone || ''}
                    readOnly
                  />
                </label>
                <button type="submit">Confirm Booking</button>
              </form>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentView;
