// StudentView.tsx
import React, { useState } from 'react';
import axios from 'axios';

interface Slot {
  id: number;
  startTime: string;
  endTime: string;
  bookedBy?: string;
  studentPhone?: string;
  coachPhone?: string;
  satisfaction?: number;
  notes?: string;
  feedbackSubmitted?: boolean;
}

interface Student {
  id: number;
  name: string;
  phone: string;
}

interface StudentViewProps {
  slots: Slot[];
  onBookSlot: (updatedSlot: Slot) => void;
  student: Student;
}

const StudentView: React.FC<StudentViewProps> = ({ slots, onBookSlot, student }) => {
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [studentName, setStudentName] = useState(student.name);
  const [studentPhone, setStudentPhone] = useState(student.phone);
  const [phoneError, setPhoneError] = useState('');

  const handleBook = async (slot: Slot) => {
    const phoneRegex = /^\d{3}-\d{3}-\d{4}$/;
    if (!studentName || !studentPhone) {
      alert('Please enter your name and phone number.');
      return;
    }
    if (!phoneRegex.test(studentPhone)) {
      setPhoneError('Phone number must be in the format XXX-XXX-XXXX.');
      return;
    }
    try {
      const response = await axios.post(`http://localhost:5001/api/slots/${slot.id}/book`, {
        bookedBy: studentName,
        studentPhone: studentPhone,
      });
      onBookSlot(response.data);
      setSelectedSlot(null); // Clear the selected slot
    } catch (error) {
      console.error('Error booking slot:', error);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric'
    });
  };

  return (
    <div className="content">
      <div className="column">
        <h2>Available Slots</h2>
        <ul>
          {slots.map(slot => (
            <li key={slot.id} className={`${slot.bookedBy ? 'booked' : ''} ${slot.feedbackSubmitted ? 'feedback-submitted' : ''}`}>
              {formatDate(slot.startTime)} - {formatDate(slot.endTime)}
              {!slot.bookedBy && <button onClick={() => setSelectedSlot(slot)}>Book</button>}
              {slot.bookedBy && <button onClick={() => setSelectedSlot(slot)}>View Details</button>}
            </li>
          ))}
        </ul>
      </div>
      <div className="column">
        {selectedSlot ? (
          <div>
            <h2>Slot Details</h2>
            {selectedSlot.bookedBy ? (
              <>
                <p><strong>Booked by:</strong> {selectedSlot.bookedBy}</p>
                <p><strong>Student's phone:</strong> {selectedSlot.studentPhone}</p>
                <p><strong>Coach's phone:</strong> {selectedSlot.coachPhone}</p>
              </>
            ) : (
              <form onSubmit={(e) => {
                e.preventDefault();
                handleBook(selectedSlot);
              }}>
                <label>
                  Your Name:
                  <input
                    type="text"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                  />
                </label>
                <label>
                  Your Phone:
                  <input
                    type="tel"
                    value={studentPhone}
                    onChange={(e) => setStudentPhone(e.target.value)}
                  />
                </label>
                {phoneError && <p style={{ color: 'red' }}>{phoneError}</p>}
                <button type="submit">Confirm Booking</button>
              </form>
            )}
          </div>
        ) : (
          <p>Select a slot to view details or book.</p>
        )}
      </div>
    </div>
  );
};

export default StudentView;
