// StudentView.tsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface Slot {
  id: number;
  startTime: string;
  endTime: string;
  bookedBy?: string;
  studentPhone?: string;
  coachPhone?: string;
  satisfaction?: number;
  notes?: string;
  feedbackSubmitted?: boolean;
  shareWithStudent?: boolean;
}

interface Student {
  id: number;
  name: string;
  phone: string;
}

interface StudentViewProps {
  slots: Slot[];
  onBookSlot: (updatedSlot: Slot) => void;
  students: Student[];
  currentStudent: Student | null;
  setCurrentStudent: React.Dispatch<React.SetStateAction<Student | null>>;
  handleCreateStudent: (name: string, phone: string) => Promise<void>;
  handleDeleteStudent: (studentId: number) => Promise<void>;
}

const StudentView: React.FC<StudentViewProps> = ({
  slots,
  onBookSlot,
  students,
  currentStudent,
  setCurrentStudent,
  handleCreateStudent,
  handleDeleteStudent,
}) => {
  const [selectedSlot, setSelectedSlot] = useState<Slot | null>(null);
  const [studentName, setStudentName] = useState('');
  const [studentPhone, setStudentPhone] = useState('');
  const [studentSlots, setStudentSlots] = useState<Slot[]>([]);

  useEffect(() => {
    if (currentStudent) {
      const fetchStudentSlots = async () => {
        try {
          const response = await axios.get(`http://localhost:5001/api/slots/student/${currentStudent.id}`);
          setStudentSlots(response.data);
        } catch (error) {
          console.error('Error fetching student slots:', error);
        }
      };
      fetchStudentSlots();
    }
  }, [currentStudent]);

  const handleBook = async (slot: Slot) => {
    if (!currentStudent) {
      alert('Please select a student.');
      return;
    }
    try {
      const response = await axios.post(`http://localhost:5001/api/slots/${slot.id}/book`, {
        bookedBy: currentStudent.name,
        studentPhone: currentStudent.phone,
        studentId: currentStudent.id,
      });
      onBookSlot(response.data);
      setSelectedSlot(null); // Clear the selected slot
    } catch (error) {
      console.error('Error booking slot:', error);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
    });
  };

  const handleAddStudent = async () => {
    try {
      const response = await axios.post('http://localhost:5001/api/students', {
        name: studentName,
        phone: studentPhone,
      });
      setStudents([...students, response.data]);
      setStudentName('');
      setStudentPhone('');
    } catch (error) {
      console.error('Error adding student:', error);
    }
  };

  return (
    <div className="content">
      <div className="column">
        <div className="add-student-container">
          <h2>Students</h2>
          <input
            type="text"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
            placeholder="Student Name"
          />
          <input
            type="tel"
            value={studentPhone}
            onChange={(e) => setStudentPhone(e.target.value)}
            placeholder="Student Phone"
          />
          <button onClick={() => handleCreateStudent(studentName, studentPhone)}>Add Student</button>
        </div>
        <select
          onChange={(e) => {
            setCurrentStudent(students.find((student) => student.id === parseInt(e.target.value)) || null);
            setSelectedSlot(null); // Reset selected slot
          }}
        >
          <option value="">Select a student</option>
          {students.map((student) => (
            <option key={student.id} value={student.id}>
              {student.name}
            </option>
          ))}
        </select>
        <ul>
          {students.map((student) => (
            <li key={student.id}>
              {student.name} ({student.phone})
              <button onClick={() => handleDeleteStudent(student.id)}>Delete</button>
            </li>
          ))}
        </ul>
        <h2>Available Slots</h2>
        <ul>
          {slots.filter((slot) => !slot.bookedBy).map((slot) => (
            <li key={slot.id} className={`${slot.bookedBy ? 'booked' : ''} ${slot.feedbackSubmitted ? 'feedback-submitted' : ''}`}>
              {formatDate(slot.startTime)} - {formatDate(slot.endTime)}
              <button onClick={() => setSelectedSlot(slot)}>Book</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="column">
        {currentStudent ? (
          <>
            <h2>{currentStudent.name}'s Slots</h2>
            <ul>
              {studentSlots.map((slot) => (
                <li key={slot.id} className={`${slot.bookedBy ? 'booked' : ''} ${slot.feedbackSubmitted ? 'feedback-submitted' : ''}`}>
                  {formatDate(slot.startTime)} - {formatDate(slot.endTime)}
                  <button onClick={() => setSelectedSlot(slot)}>View Details</button>
                </li>
              ))}
            </ul>
          </>
        ) : (
          <p>Select a student to view their slots.</p>
        )}
      </div>
    </div>
  );
};

export default StudentView;
