// src/components/RecordFeedback.tsx

import React, { useState } from 'react';
import axios from 'axios';

interface Slot {
  id: number;
  startTime: string;
  endTime: string;
  bookedBy?: string;
  studentPhone?: string;
  coachPhone?: string;
  satisfaction?: number;
  notes?: string;
  feedbackSubmitted?: boolean;
}

interface RecordFeedbackProps {
  slot: Slot;
  onSubmit: (updatedSlot: Slot) => void;
}

const RecordFeedback: React.FC<RecordFeedbackProps> = ({ slot, onSubmit }) => {
  const [satisfaction, setSatisfaction] = useState(slot.satisfaction || 3);
  const [notes, setNotes] = useState(slot.notes || '');

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    const updatedSlot = { ...slot, satisfaction, notes, feedbackSubmitted: true };

    try {
      const response = await axios.post(`http://localhost:5001/api/slots/${slot.id}/feedback`, {
        satisfaction,
        notes,
      });
      onSubmit(response.data);
    } catch (error) {
      console.error('Error submitting feedback:', error);
    }
  };

  return (
    <div>
      <h2>Record Feedback for Slot</h2>
      {slot.bookedBy ? (
        <form onSubmit={handleSubmit}>
          <label>
            Satisfaction (1-5):
            <input
              type="number"
              value={satisfaction}
              onChange={(e) => setSatisfaction(Number(e.target.value))}
              min="1"
              max="5"
            />
          </label>
          <label>
            Notes:
            <textarea
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </label>
          <button type="submit">Submit Feedback</button>
        </form>
      ) : (
        <p>The slot must be booked by a student before you can submit feedback.</p>
      )}
    </div>
  );
};

export default RecordFeedback;
